.masthead {
  height: 100vh;
  width: 100vw;
  position: relative;
  z-index: -1;
  background-image: url('../../assets/img/main-picture.jpg');
  background-repeat: no-repeat;
  background-position: top center;
  background-size: cover;
  background-attachment: fixed;

  .masthead__overlay {
    background: rgb(14, 39, 67);
    background: linear-gradient(180deg, rgba(14, 39, 67, .7) 30%, rgba(25, 80, 97, 0.8) 70%, rgba(14, 39, 67, 1) 100%);
    overflow: hidden;
    z-index: 2;
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;

    .masthead__logo {
      width: 100%;
      height: 100%;
      z-index: 30;
      background-image: url('../../assets/img/main-logo.png');
      background-position: center center;
      background-repeat: no-repeat;
      @include transition(opacity 500ms ease-in-out);

      @media all and (orientation: portrait) {
        background-size: auto 65vw;
      }

      @media all and (orientation: landscape) {
        background-size: 65vh auto;
      }

      &.logo--hidden{
        opacity: 0;
      }
    }
  }
}

